<?phpclass BaseAction extends Action{    protected $grouplist = array();    protected $config = array();    protected $wx = null;    function _initialize()    {        flash();        $allow_act = explode(",", "login,loginOn,loginOff");        $cookie = cookie('login_user');        if ($cookie)            session('login_user', $cookie);        if (!in_array(ACTION_NAME, $allow_act)) {            $uname = session('login_user.name');            if (empty($uname)) {                exit('<script>window.top.location="' . U('Index/login') . '";</script>');            }        }        $this->config = get_config();    }}